
public class City {
	 private String name;
	 private int locId;
	 private int id;
   
   public City() {
	   
   }
   public City(String name, int locId, int id){
	   this.id = id;
	   this.name = name;
	   this.locId = locId;
	   
   }

public String getName() {
	return name;
}



public int getLocId() {
	return locId;
}



public int getId() {
	return id;
}


   
   
}
