
public class Passenger {
   private int numOfPas;
   private int startId;
   private int destId;
   
   public Passenger(){
	   
   }

public Passenger(int numOfPas, int startId, int destId) {
	this.numOfPas = numOfPas;
	this.startId = startId;
	this.destId = destId;
}

public int getNumOfPas() {
	return numOfPas;
}



public int getStartId() {
	return startId;
}



public int getDestId() {
	return destId;
}


   
   
}
